function Icon(defaultClass = "") {

   const getIconsWrapper = () => {
      return $('#svg-defs')
   }

   const isValidIcon = (icon) => {
      return getIconsWrapper()
         .children('#' + icon)
         .length > 0
   }

   const iconClass = defaultClass.split(' ').filter(Boolean).find(isValidIcon)

   //Elementos
   const svgElem = $(document.createElementNS('http://www.w3.org/2000/svg', 'svg'))
   const useElem = document.createElementNS('http://www.w3.org/2000/svg', 'use')

   svgElem.append(useElem)
   useElem.setAttributeNS('http://www.w3.org/1999/xlink', 'xlink:href', '#' + (iconClass ? iconClass : 'warning'));
   svgElem.addClass(defaultClass)
   svgElem.css('color', iconClass ? '' : "var(--danger)")

   new MutationObserver(() => {

      const classes = (svgElem.attr('class') ?? "").split(' ').reverse()
      const newIconClass = classes.find(className => !!$(`#svg-defs > #${className}`).length)

      if (!newIconClass) {
         return
      }

      useElem.setAttributeNS('http://www.w3.org/1999/xlink', 'xlink:href', '#' + (newIconClass ? newIconClass : 'warning'));
      svgElem.css('color', newIconClass ? '' : "var(--danger)")

   }).observe(svgElem[0], {
      attributes: true,
      attributeFilter: ['class']
   })

   return svgElem
}